'use strict';

const BaseClientFactory = require('./baseClientFactory');

class AWSCognitoFactory extends BaseClientFactory {
    constructor() {
        super();
    }

    build(name) {
        const { Cognito } = require('@tenna-llc/aws-sdk-wrapper');

        return new Cognito(super.get(name));
    }
}

module.exports = AWSCognitoFactory;
