'use strict';

const BaseClientFactory = require('./baseClientFactory');

class AWSDynamoDBClientFactory extends BaseClientFactory {
    constructor() {
        super();
    }

    build(name) {
        const { DynamoDB } = require('@tenna-llc/aws-sdk-wrapper');

        return new DynamoDB(super.get(name));
    }
}

module.exports = AWSDynamoDBClientFactory;
