'use strict';

const BaseClientFactory = require('./baseClientFactory');

class SuperAgentClientFactory extends BaseClientFactory {
    constructor() {
        super();
    }

    build(name) {
        const { ProxyAgent } = require('@tenna-llc/superagent-wrapper');

        return new ProxyAgent(super.get(name));
    }
}

module.exports = SuperAgentClientFactory;
